mapper.select:
select article_no, user_id, subject, content, hit, register_time 
from board
where subject = #{article_no}

mapper.update:
		update board
	    set hit = hit + 1
   		where article_no = #{articleNo}

		update board
    	set user_id = #{userId},
        subject = #{subject},
        content = #{content}
    	where article_no = #{articleNo}
